import React, { useState } from 'react';
import PropertyListing from './PropertyListing'; // Import your PropertyListing component

function ListingPage() {
  // State to store property listings
  const [listings, setListings] = useState([]);

  // State to store review data
  const [reviewFormData, setReviewFormData] = useState({
    rating: 0,
    comment: '',
  });

  // Function to handle changes in the review form
  const handleReviewChange = (e) => {
    const { name, value } = e.target;
    setReviewFormData({ ...reviewFormData, [name]: value });
  };

  // Function to submit a review
  const submitReview = (e) => {
    e.preventDefault();
    // Add logic to post the review data to your backend API
    // You'll need to implement the API endpoint for posting reviews
    // and handle the review data here
    console.log('Review submitted:', reviewFormData);
  };

  // Function to toggle favoriting a listing
  const toggleFavorite = (listingId) => {
    // Add logic to handle favorite/unfavorite a listing
    // You'll need to implement this functionality
    console.log(`Toggle favorite for listing ID ${listingId}`);
  };

  return (
    <div>
      <h1>Listing Page</h1>
      {/* Render the PropertyListing component to display property listings */}
      <PropertyListing />

      <h2>Post a Review</h2>
      <form onSubmit={submitReview}>
        <label>
          Rating:
          <input
            type="number"
            name="rating"
            value={reviewFormData.rating}
            onChange={handleReviewChange}
          />
        </label>
        <label>
          Comment:
          <textarea
            name="comment"
            value={reviewFormData.comment}
            onChange={handleReviewChange}
          />
        </label>
        <button type="submit">Post Review</button>
      </form>

      <h2>Property Listings</h2>
      <ul>
        {listings.map((listing) => (
          <li key={listing.id}>
            <img src={listing.image_url} alt={listing.title} />
            <h2>{listing.title}</h2>
            <p>{listing.description}</p>
            <p>Location: {listing.city}, {listing.state} {listing.zip_code}</p>
            <p>Price: ${listing.price} per night</p>
            <button onClick={() => toggleFavorite(listing.id)}>
              {listing.isFavorite ? 'Unfavorite' : 'Favorite'}
            </button>
            <form onSubmit={submitReview}>
              <label>
                Rating:
                <input
                  type="number"
                  name="rating"
                  value={reviewFormData.rating}
                  onChange={handleReviewChange}
                />
              </label>
              <label>
                Comment:
                <textarea
                  name="comment"
                  value={reviewFormData.comment}
                  onChange={handleReviewChange}
                />
              </label>
              <button type="submit">Post Review</button>
            </form>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ListingPage;
