import React, { useState, useEffect } from 'react';

const PropertyListing = ({ id, image_url, title, description, city, state, zip_code, price, onFavoriteClick, isFavorite }) => (
  <div>
    <h1>Property Listing</h1>
    <ul>
      <li>
        <img src={image_url} alt="rental property" />
        <h2>{title}</h2>
        <p>Description: {description}</p>
        <p>City: {city}</p>
        <p>State: {state}</p>
        <p>Zip Code: {zip_code}</p>
        <p>Price: {price}</p>
        <button onClick={() => onFavoriteClick(id)}>{isFavorite ? 'Remove from Favorites' : 'Add to Favorites'}</button>
      </li>
    </ul>
  </div>
);

const ListingPage = ({ list, onFavoriteClick, favoriteList, isFavoriteView }) => {
  if (!Array.isArray(list) || list.length === 0) {
    return (
      <div>
        <h1>No Property Listings Found</h1>
      </div>
    );
  }

  const rental = list.map((rental) => (
    <PropertyListing
      key={rental.id}
      onFavoriteClick={onFavoriteClick}
      isFavorite={favoriteList.includes(rental.id)}
      {...rental}
    />
  ));

  return (
    <div>
      <h1>Property Listings</h1>
      <ul>
        {isFavoriteView ? rental.filter((item) => favoriteList.includes(item.id)) : rental}
      </ul>
    </div>
  );
};

const Listings = () => {
  const [listings, setListings] = useState([]);
  const [favoriteList, setFavoriteList] = useState([]);
  const [isFavoriteView, setIsFavoriteView] = useState(false);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/listings')
      .then((response) => response.json())
      .then((data) => {
        setListings(data);
      })
      .catch((error) => {
        console.error('Error fetching property listings:', error);
      });
  }, []);

  const handleFavoriteClick = (id) => {
    if (favoriteList.includes(id)) {
      setFavoriteList(favoriteList.filter((item) => item !== id));
    } else {
      setFavoriteList([...favoriteList, id]);
    }
  };

  const toggleView = () => {
    setIsFavoriteView(!isFavoriteView);
  };

  return (
    <div>
      <button onClick={toggleView}>
        {isFavoriteView ? 'View All Listings' : 'View Favorite Listings'}
      </button>
      <div>
        {isFavoriteView && <h1>Favorites</h1>}
        <ListingPage list={listings} onFavoriteClick={handleFavoriteClick} favoriteList={favoriteList} isFavoriteView={isFavoriteView} />
      </div>
    </div>
  );
};

export default Listings;
