import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import Header from '../pages/Header';

const DisplayRentals = ({ list }) => {
  const [rental, setRental] = useState([]);
  const [checkInDate, setCheckInDate] = useState(new Date());
  const [checkOutDate, setCheckOutDate] = useState(new Date());
  const [isBookingModalOpen, setIsBookingModalOpen] = useState(false);
  const [booking, setBooking] = useState([]);

  const initMap = () => {
    const googleMapsScript = document.createElement('script');
    googleMapsScript.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&callback=initMap`;
    googleMapsScript.async = true;
    googleMapsScript.defer = true;
    document.head.appendChild(googleMapsScript);

    window.initMap = () => {
      const map = new window.google.maps.Map(document.getElementById('map'), {
        center: { lat: 0, lng: 0 },
        zoom: 5,
      });

      rental.forEach((property) => {
        const marker = new window.google.maps.Marker({
          position: { lat: parseFloat(property.latitude), lng: parseFloat(property.longitude) },
          map,
          title: property.title,
        });
      });
    };
  };

  const handleBooking = (booking) => {
    setBooking(booking);
    setIsBookingModalOpen(true);
  };

  const handleConfirmBooking = () => {
    if (checkInDate && checkOutDate) {
      const bookingData = {
        listing_id: booking,
        check_in: checkInDate,
        check_out: checkOutDate,
      };
      if (checkInDate && checkOutDate) {
        const addUserToFormData = { ...bookingData, user_id: localStorage.id };
        fetch('http://127.0.0.1:5000/bookings', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(addUserToFormData),
        })
          .then((response) => response.json())
          .then((data) => {
            console.log(data);
            setIsBookingModalOpen(false);
          });
      } else {
        alert('Please select a check-in and check-out date.');
      }
    }
  };

  useEffect(() => {
    fetch('http://127.0.0.1:5000/listings')
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setRental(data);
        initMap();
      })
      .catch((error) => {
        console.error('Error fetching property listings:', error);
      });
  }, []);

  console.log(list);
  const handleFavorite = (listingId) => {
    const favoriteData = {
      user_id: localStorage.id,
      listing_id: listingId,
    };

    fetch('http://127.0.0.1:5000/favorites', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(favoriteData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
      });
  };

  return (
    <div>
      <Header />
      <h1>All Rentals</h1>
      {rental.map((property) => (
        <div key={property.id} className="rental-listing">
          <img src={property.image_url} alt="rental property" />
          <h2>{property.title}</h2>
          <p>Description: {property.description}</p>
          <p>City: {property.city}</p>
          <p>State: {property.state}</p>
          <p>Zip Code: {property.zip_code}</p>
          <p>Price: {property.price}</p>
          <button onClick={() => handleFavorite(property.id)}>Add to Favorites</button>
          <button onClick={() => handleBooking(property.id)}>Book Now</button>
        </div>
      ))}

      <div id="map" style={{ width: '100%', height: '400px' }}></div>
    </div>
  );
};

export default DisplayRentals;
