import React, { useEffect, useState } from "react";
import Header from '../pages/Header';

const AccountSettings = () => {
    const [user, setUser] = useState([]);
    const [updateUserFormData, setUpdateUserFormData] = useState({
        first_name: '',
        last_name: '',
        email: '',
        password: '',
        phone_number: '',
    });

    useEffect(() => {
        fetch('http://127.0.0.1:5000/user')
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                setUser(data);
            })
            .catch((error) => {
                console.error('Error fetching user:', error);
            });
    }, []); // Add an empty dependency array to run the effect only once

    const handleUpdateUserFormChange = (event) => {
        const addUserToFormData = { ...updateUserFormData, user_id: localStorage.id };
        const fieldName = event.target.name;
        const fieldValue = event.target.value;
        const newUserFormData = { ...updateUserFormData };
        newUserFormData[fieldName] = fieldValue;
        setUpdateUserFormData(newUserFormData);
        fetch('http://127.0.0.1:5000/user', {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(addUserToFormData),
        })
        .then((response) => response.json())
        .then((data) => {
            console.log(data);
            setUser(data);
            setUpdateUserFormData({
                first_name: '',
                last_name: '',
                email: '',
                password: '',
                phone_number: '',
            });
        });
    }

    return (
        <div>
            <Header />
            <h1>Account Settings</h1>
            {user.map((userData) => (
                <div key={userData.user_id}>
                    <h2>First Name: {userData.first_name} Last Name: {userData.last_name}</h2>
                    <h3>Email: {userData.email}</h3>
                    <h3>Phone Number: {userData.phone_number}</h3>
                    <h3>Password: {userData.password}</h3>
                    <button onClick={handleUpdateUserFormChange}>Update</button>
                    <div>
                        <label htmlFor="first_name">First Name</label>
                        <input
                            type="text"
                            name="first_name"
                            value={updateUserFormData.first_name}
                            onChange={handleUpdateUserFormChange}
                        />
                        <label htmlFor="last_name">Last Name</label>
                        <input
                            type="text"
                            name="last_name"
                            value={updateUserFormData.last_name}
                            onChange={handleUpdateUserFormChange}
                        />
                        <label htmlFor="email">Email</label>
                        <input
                            type="text"
                            name="email"
                            value={updateUserFormData.email}
                            onChange={handleUpdateUserFormChange}
                        />
                        <label htmlFor="password">Password</label>
                        <input
                            type="text"
                            name="password"
                            value={updateUserFormData.password}
                            onChange={handleUpdateUserFormChange}
                        />
                        <label htmlFor="phone_number">Phone Number</label>
                        <input
                            type="text"
                            name="phone_number"
                            value={updateUserFormData.phone_number}
                            onChange={handleUpdateUserFormChange}
                        />
                    </div>
                </div>
            ))}
        </div>
    );
};

export default AccountSettings;
