import React, { useEffect, useState } from 'react';
import 'react-datepicker/dist/react-datepicker.css';
import Header from '../pages/Header';

const RentalMap = ({ rental }) => {
  const [map, setMap] = useState(null);

  useEffect(() => {
    const mapOptions = {
      center: { lat: parseFloat(rental.latitude), lng: parseFloat(rental.longitude) },
      zoom: 10,
    };

    const map = new window.google.maps.Map(document.getElementById(`map-${rental.id}`), mapOptions);

    // Add a marker for the rental
    const marker = new window.google.maps.Marker({
      position: mapOptions.center,
      map,
      title: rental.title,
    });

    setMap(map);
  }, [rental]);

  return <div id={`map-${rental.id}`} style={{ width: '100%', height: '200px' }} />;
};

const DisplayRentals = ({ list }) => {
  const [rentals, setRentals] = useState([]);
  const [isBookingModalOpen, setIsBookingModalOpen] = useState(false);
  const [booking, setBooking] = useState([]);

  const handleBooking = (booking) => {
    setBooking(booking);
    setIsBookingModalOpen(true);
  };

  const handleConfirmBooking = () => {
    if (checkInDate && checkOutDate) {
      const bookingData = {
        listing_id: booking,
        check_in: checkInDate,
        check_out: checkOutDate,
      };
      if (checkInDate && checkOutDate) {
        const addUserToFormData = { ...bookingData, user_id: localStorage.id };
        fetch('http://127.0.0.1:5000/bookings', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(addUserToFormData),
        })
          .then((response) => response.json())
          .then((data) => {
            console.log(data);
            setIsBookingModalOpen(false);
          });
      } else {
        alert('Please select a check-in and check-out date.');
      }
    }
  };

  const initMap = () => {
    // Load the Google Maps JavaScript API using your API key
    const googleMapsScript = document.createElement('script');
    googleMapsScript.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&callback=initMap`;
    googleMapsScript.async = true;
    googleMapsScript.defer = true;
    document.head.appendChild(googleMapsScript);
  };

  useEffect(() => {
    fetch('http://127.0.0.1:5000/listings')
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setRentals(data);
        initMap(); // Call the initMap function after fetching data
      })
      .catch((error) => {
        console.error('Error fetching property listings:', error);
      });
  }, []);

  return (
    <div>
      <Header />
      <h1>All Rentals</h1>
      {rentals.map((rental) => (
        <div key={rental.id} className="rental-listing">
          <RentalMap rental={rental} />
          <img src={rental.image_url} alt="rental property" />
          <h2>{rental.title}</h2>
          <p>Description: {rental.description}</p>
          <p>City: {rental.city}</p>
          <p>State: {rental.state}</p>
          <p>Zip Code: {rental.zip_code}</p>
          <p>Price: {rental.price}</p>
          <button onClick={() => handleBooking(rental.id)}>Book Now</button>
          {isBookingModalOpen && (
            <div className="booking-modal">
              <div className="booking-modal-content">
                <div className="booking-modal-header">
                  <h2>Book Now</h2>
                </div>
                <div className="booking-modal-body">
                  {/* ... (booking form inputs) */}
                </div>
              </div>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default DisplayRentals;
