import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';

const Bookings = () => {
    const navigate = useNavigate();
    const [bookings, setBookings] = useState([]);
    const [selectedListing, setSelectedListing] = useState(null);
    const [updateBookingData, setUpdateBookingData] = useState({
        title: '',
        description: '',
        city: '',
        state: '',
        zip_code: '',
        price: '',
    });

    useEffect(() => {
        fetch(`http://127.0.0.1:5000/user/${localStorage.getItem('id')}/bookings`)
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                setBookings(data);
            })
            .catch((error) => {
                console.error('Error fetching property bookings:', error);
            });
    }, []);

    console.log(bookings);
    const handleDeleteBooking = (user_id, booking_id) => {
        fetch(`http://127.0.0.1:5000/user/${user_id}/bookings/${booking_id}`, {
          method: 'DELETE',
        })
        .then(response => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then(data => {
          console.log('Booking deleted successfully:', data);
        })
        .catch(error => {
          console.error('There has been a problem with your fetch operation:', error);
        });
       };
    const handleUpdateFormChange = (event) => {
        const fieldName = event.target.name;
        const fieldValue = event.target.value;
        const newFormData = { ...updateBookingData };
        newFormData[fieldName] = fieldValue;
        setUpdateBookingData(newFormData);
    };

    const handleUpdateListing = () => {
        const addUserToFormData = { ...updateBookingData, user_id: localStorage.id };
        fetch(`http://127.0.0.1:5000/bookings/${selectedListing.id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(addUserToFormData),
        })
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                setBookings(data);
                setSelectedListing(null);
                setUpdateBookingData({
                    title: '',
                    description: '',
                    city: '',
                    state: '',
                    zip_code: '',
                    price: '',
                    image_url: '',
                });
            })
            .catch((error) => {
                console.error('Error updating property listing:', error);
            });
    };

    return (
        <div>
            <Header />
            <h1>bookings</h1>
            <div>
            {bookings.map((booking) => (
                <div key={booking.id} className="booking">
                <h2>Booking for {booking.listing.title}</h2>
                <a>Image: <img src={booking.listing.image_url} alt="Listing" /></a>
                <p>Description: {booking.listing.description}</p>
                <p>Address: {booking.listing.address}</p>
                <p>City: {booking.listing.city}</p>
                <p>State: {booking.listing.state}</p>
                <p>Zip Code: {booking.listing.zip_code}</p>
                <p>Country: {booking.listing.country}</p>

                <p>Price: {booking.listing.price}</p>
                <p>Check-in: {booking.check_in}</p>
                <p>Check-out: {booking.check_out}</p>
                <button onClick={() => handleDeleteListing(booking.id)}>Delete</button>
                <button onClick={() => setSelectedListing(booking)}>Update</button>
   </div>
))}
            </div>
        </div>
    );
};

export default Bookings;
